import { createContext, useState, useEffect } from 'react';
import axios from 'axios';


const getDefaultCart = () => {
  // Return an empty object to represent an empty cart
  return {};
};

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cartItems,setCartItems]=useState(getDefaultCart());
  const [loading, setLoading] = useState(false);
  const token = localStorage.getItem('token'); // Extract token to a separate variable

  useEffect(() => {
    console.log("Cart updated:", cartItems);
    fetchCartData(); // Retrieve cart data on page load

    const fetchCartItems = async () => {
      if (token) {
        try {
          const response = await fetch('http://localhost:4000/getcart', {
            method: 'POST',
            headers: {
              token,
            },
          });
          const data = await response.json();
          setCartItems(data); // Update cartItems state with received data
        } catch (error) {
          console.error(error);
        }
      }
    };
    fetchCartItems();
  },[cartItems,token]);


  


  const fetchCartData = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:4000/getcart', {});
      const cartData = response.data;
      setCartItems(cartData);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev, [itemId]:prev[itemId]+1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/addtocart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  const removefromcart = (itemId) => {
    setCartItems((prev) => ({...prev,[itemId]:prev[itemId]-1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/removefromcart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  if (loading) { // <--- Display loading indicator while fetching data
    return <div>Loading...</div>;
  }

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removefromcart  }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
