import { useParams } from 'react-router-dom'; 
import React, { useState, useEffect } from 'react';

function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const { productId } = useParams();

  useEffect(() => {
    fetchProduct(productId)
      .then(data => setProduct(data))
      .catch(error => setError(error.message));
  }, [productId]);
  if (error) {
    return <div>Error: {error}</div>; 
  }
  if (!product) {
    return <div>Loading product details...</div>;
  }


  async function fetchProduct(productId) {
    try {
      const headers = {
        'Content-Type': 'application/json'
      };
      const response = await fetch(`/product/${productId}`, { headers });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      if (!response.headers.get('Content-Type').includes('application/json')) {
        throw new Error('Invalid response content type. Expected application/json');
      }
  
      const data = await response.json(); 
  
      return data; 
    } catch (error) {
      console.error('Error fetching product:', error);
      throw error; 
    }
  }

  
  return (
    <div className="product-details">
      <img alt="" URL={product.image} className="img-fluid product-thumbnail" />
      <h3 className="product-title">{product.name}</h3>
      <strong className="product-price">Rs{product.new_price}.00</strong>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
