import React from 'react';
import { useContext } from 'react';
import { CartContext } from './CartContext';

const CartContent = () => {
  const { cartItems, removeFromCart, checkout } = useContext(CartContext);

  return (
    <div>
      <HeroSection />
      <CartTableSection cartItems={cartItems} removeFromCart={removeFromCart} />
      <CartActionSection />
      <CartSummarySection cartItems={cartItems} checkout={checkout} />
    </div>
  );
};

const HeroSection = () => {
  return (
    <div classNameName="hero">
      <div classNameName="container">
        <div classNameName="row justify-content-between">
          <div classNameName="col-lg-5">
            <div classNameName="intro-excerpt">
              <h1>Cart</h1>
            </div>
          </div>
          <div classNameName="col-lg-7">
          </div>
        </div>
      </div>
    </div>
  );
};


const CartSummarySection = ({ cartItems, checkout }) => {
  const subtotal = Object.keys(cartItems).reduce((acc, key) => acc + cartItems[key].price * cartItems[key].quantity, 0);
  const total = subtotal;
}
const CartTableSection = ({ cartItems, removeFromCart }) => {
  return (
    <div classNameName="untree_co-section before-footer-section">
      <div classNameName="container">
        <div classNameName="row mb-5">
          <form classNameName="col-md-12" method="post">
            <div classNameName="site-blocks-table">
              <table classNameName="table">
                <thead>
                  <tr>
                    <th classNameName="product-thumbnail">Image</th>
                    <th classNameName="product-name">Product</th>
                    <th classNameName="product-price">Price</th>
                    <th classNameName="product-quantity">Quantity</th>
                    <th classNameName="product-total">Total</th>
                    <th classNameName="product-remove">Remove</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.keys(cartItems).map((key, index) => (
                    <tr key={index}>
                      <td classNameName="product-thumbnail">
                        <img src={cartItems[key].image} alt="" classNameName="img-fluid" />
                      </td>
                      <td classNameName="product-name">
                        <h2 classNameName="h5 text-black">{cartItems[key].name}</h2>
                      </td>
                      <td>${cartItems[key].price}</td>
                      <td>
                        <div classNameName="input-group mb-3 d-flex align-items-center quantity-container" style={{ maxWidth: '120px' }}>
                          <div classNameName="input-group-prepend">
                            <button classNameName="btn btn-outline-black decrease" type="button">&minus;</button>
                          </div>
                          <input type="text" classNameName="form-control text-center quantity-amount" value={cartItems[key].quantity} placeholder="" aria-label="Example text with button addon" aria-describedby="button-addon1" />
                          <div classNameName="input-group-append">
                            <button classNameName="btn btn-outline-black increase" type="button">&plus;</button>
                          </div>
                        </div>
                      </td>
                      <td>${cartItems[key].price * cartItems[key].quantity}</td>
                      <td><button onClick={() => removeFromCart(key)} classNameName="btn btn-black btn-sm">X</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

const CartActionSection = () => {
  return (
    <div classNameName="row">
      <div classNameName="col-md-6">
        <div classNameName="row mb-5">
          <div classNameName="col-md-6 mb-3 mb-md-0">
            <button classNameName="btn btn-black btn-sm btn-block">Update Cart</button>
          </div>
          <div classNameName="col-md-6">
            <button classNameName="btn btn-outline-black btn-sm btn-block">Continue Shopping</button>
          </div>
        </div>
        <div classNameName="row">
          <div classNameName="col-md-12">
            <label classNameName="text-black h4" for="coupon">Coupon</label>
            <p>Enter your coupon code if you have one.</p>
          </div>
          <div classNameName="col-md-8 mb-3 mb-md-0">
            <input type="text" classNameName="form-control py-3" id="coupon" placeholder="Coupon Code" />
          </div>
          <div classNameName="col-md-4">
            <button classNameName="btn btn-black">Apply Coupon</button>
          </div>
        </div>
      </div>
    </div>
  );
  
};
  export default CartContent;
