import React from 'react';
import { useContext } from 'react';
import { CartContext } from './CartContext';

const CartContent = () => {
  const { cartItems,cartData, removeFromCart, checkout } = useContext(CartContext);
  if (!cartItems) return <div className="cart-container">No items in cart</div>;

  return (
    <div className="cart-container">
      <h2>Shopping Cart</h2>
      <h2>Cart Data</h2>
      {cartData ? (
        <pre>{JSON.stringify(cartData, null, 2)}</pre>
      ) : (
        <p>Loading...</p>
      )}

      <ul className="cart-items">
        {Object.keys(cartItems).map((key, index) => (
          <li key={index}>
            <div className="cart-item">
              <img src={cartItems[key].image} alt={cartItems[key].name} />
              <div className="cart-item-info">
                <h3>{cartItems[key].name}</h3>
                <p>Quantity: {cartItems[key].quantity}</p>
                <p>Price: Rs{cartItems[key].price}</p>
              </div>
              <button onClick={() => removeFromCart(key)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
      <div className="cart-summary">
        <h3>Subtotal: Rs{Object.keys(cartItems).reduce((acc, key) => acc + cartItems[key].price * cartItems[key].quantity, 0)}</h3>
        <button onClick={checkout}>Checkout</button>
      </div>
    </div>
  );
};

export default CartContent;
