import React from 'react';
import { useContext } from 'react';
import { CartContext } from './CartContext';

const CartContent = () => {
  const { cartItems, removeFromCart, checkout } = useContext(CartContext);

  console.log('cartItems:', cartItems);

  if (!cartItems) return <div className="cart-container">No items in cart</div>;

  return (
    <div className="cart-container">
      <h2>Shopping Cart</h2>
      <ul className="cart-items">
        {Object.values(cartItems).map((item, index) => {
          console.log('item:', item);
          return (
            <li key={index}>
              <div className="cart-item">
                <img src={item.image} alt={item.name} />
                <div className="cart-item-info">
                  <h3>{item.name}</h3>
                  <p>Quantity: {item.quantity}</p>
                  <p>Price: Rs{item.price}</p>
                </div>
                <button onClick={() => removeFromCart(index)}>Remove</button>
              </div>
            </li>
          );
        })}
      </ul>
      <div className="cart-summary">
        <h3>
          Subtotal: Rs
          {Object.values(cartItems).reduce((acc, item) => acc + item.price * item.quantity, 0)}
        </h3>
        <button onClick={checkout}>Checkout</button>
      </div>
    </div>
  );
};

export default CartContent;
