import React from 'react';
import { useContext } from 'react';
import { CartContext } from './CartContext';

const CartContent = () => {
  const { cartItems, removeFromCart, checkout } = useContext(CartContext);
  console.log('cartItems:', cartItems); // Add this line

  if (!cartItems) return <div className="cart-container">No items in cart</div>;

  return (
    <div className="cart-container">
      <h2>Shopping Cart</h2>
      <ul className="cart-items">
  {Object.keys(cartItems).map(key => {
    if (cartItems[key].quantity > 0) {
      return (
        <li key={key}>
          ...
        </li>
      );
    }
    return null;
  })}
</ul>
      <div className="cart-summary">
        <h3>
          Subtotal: Rs
          {Object.keys(cartItems).filter(key => cartItems[key].quantity > 0).reduce((acc, key) => acc + cartItems[key].price * cartItems[key].quantity, 0)}
        </h3>
        <button onClick={checkout}>Checkout</button>
      </div>
    </div>
  );
};

export default CartContent;
