import { useParams } from 'react-router-dom'; // Import useParams for accessing route parameters
import React, { useState, useEffect } from 'react';
// ... other imports

function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const { productId } = useParams();

  useEffect(() => {
    fetchProduct(productId)
      .then(data => setProduct(data))
      .catch(error => setError(error.message));
  }, [productId]);
  if (error) {
    return <div>Error: {error}</div>; // Display the error message
  }
  if (!product) {
    return <div>Loading product details...</div>;
  }



  async function fetchProduct(productId) {
    try {
      const headers = {
        'Content-Type': 'application/json'
      };
      const response = await fetch(`/product/${productId}`, { headers });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      // Check for valid JSON content type header
      if (response.headers.get('Content-Type') !== 'application/json') {
        throw new Error('Invalid response content type. Expected application/json');
      }
  
      const data = await response.json(); // Get the response as JSON
  
      return data; // Return the parsed product
    } catch (error) {
      console.error('Error fetching product:', error);
      throw error; // Re-throw the error for handling in the component
    }
  }



  
  return (
    <div className="product-details">
      <img alt="" src={product.image} className="img-fluid product-thumbnail" />
      <h3 className="product-title">{product.name}</h3>
      <strong className="product-price">Rs{product.new_price}.00</strong>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
