import { createContext, useState, useEffect } from 'react';
import axios from 'axios';


const getDefaultCart = () => {
  // Return an empty object to represent an empty cart
  return {};
};

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cartItems,setCartItems]=useState(getDefaultCart());

  useEffect(() => {
    console.log("Cart updated:", cartItems);
    fetchCartData(); // Retrieve cart data on page load
    
  }, [cartItems]);


  


  const fetchCartData = async () => {
    const token = localStorage.getItem('token');
    console.log('token:', token); // Log the token to verify its existence

  if (token) {
    try {
      const response = await axios.post('http://localhost:4000/getcart', {
        method:'POST',
        headers: {
          Accept: 'application/json',
          token:localStorage.getItem('token'),
          'Content-Type': 'application/json',
        },
      });
      const cartData = response.data;
      setCartItems(cartData);
    } catch (error) {
      if (error.response) {
        // handle error response
        console.error(error.response.data);
      } else {
        // handle other errors
        console.error(error);
      }
    }
  } else {
    console.error('Token not found in local storage');
  }
};

  

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev, [itemId]:prev[itemId]+1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/addtocart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  const removefromcart = (itemId) => {
    setCartItems((prev) => ({...prev,[itemId]:prev[itemId]-1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/removefromcart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removefromcart  }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
