import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cart, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log("Cart updated:", cart);
    fetchCartData(); // Retrieve cart data on page load
  }, [cart]);

  const fetchCartData = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/getcart', {});
      const cartData = response.data;
      setCartItems(cartData);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const updateCartData = async (newCartData) => {
    try {
      const response = await axios.post('/updatecart', { cartData: newCartData });
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev, [itemId]:prev[itemId]+1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/addtocart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  const removeItemFromCart = (productId) => {
    setCartItems((prevCart) => prevCart.filter((item) => item.id!== productId));
    updateCartData(cart.filter((item) => item.id!== productId)); // Update cart data on server
  };

  if (loading) { // <--- Display loading indicator while fetching data
    return <div>Loading...</div>;
  }

  return (
    <CartContext.Provider value={{ cart, addToCart, removeItemFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
