import React from 'react'
import Navbar from '../Navbar'
import Footer from '../Footer'
import { useState } from 'react'
import { useContext } from 'react'
import { CartContext } from '../CartContext';
import { useEffect } from 'react'


const OrderPayment = () => {
        const [name, setName] = useState('');
        const [phoneNumber, setPhoneNumber] = useState('');
        const [amount, setAmount] = useState(0);
        const { getTotalCartAmount} = useContext(CartContext);
        const currency = "INR";
        const receiptId = "qwes1";
   

        const paymenthandler = async()=>{
            const response = await fetch("http://localhost:4000/order",{
                method:"POST",
                body: JSON.stringify({
                    amount,
                    currency,
                    receipt:receiptId,
                }),
                headers:{
                    "Content-Type": "application/json",
                },
            });
            const order = await response.json();
            console.log(order);    
        };


        const handleSubmit = (event) => {
            event.preventDefault();
            // Add your payment logic here
            console.log('Payment submitted:', name, phoneNumber, amount);
          };

          useEffect(() => {
            setAmount(getTotalCartAmount());
          }, [getTotalCartAmount]);

  return (
    <div>
        <Navbar/>
        <div>
        <div className="App">
      <form id="payment-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="phoneNumber">Phone Number</label>
          <input
            type="tel"
            className="form-control"
            value={phoneNumber}
            onChange={(event) => setPhoneNumber(event.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="amount">Amount to be Paid</label>
          <p>Amount: {amount}</p>
      <p>Total Cart Amount: {getTotalCartAmount()}</p>
        </div>
        <p>Total Cart Amount: {getTotalCartAmount()}</p>
        <button type="submit" onClick={paymenthandler} className="btn btn-primary">
          Pay
        </button>
      </form>
    </div>
        </div>
        <Footer/>
    </div>
  )
}

export default OrderPayment
