import React from 'react'
import { useState } from 'react';

function FabricCare() {
  return (
    <div>
      <h2>Fabric Care</h2>
      <p>Please follow these care instructions to ensure the longevity of your curtains:</p>
        <li>Dry clean only</li>
        <li>Avoid direct sunlight</li>
        <li>Do not iron or steam</li>
    </div>
  );
}

function SizeOptions() {
  const [size, setSize] = useState('');
  const [customSize, setCustomSize] = useState({ width: '', height: '' });

  const handleSizeChange = (event) => {
    setSize(event.target.value);
  };

  const handleCustomSizeChange = (event) => {
    setCustomSize({ ...customSize, [event.target.name]: event.target.value });
  };

  

  return (
    <div>
      <h2>Size Options</h2>
      <p>Please select a size from the options below:</p>
      <select value={size} onChange={handleSizeChange}>
        <option value="5ft">5ft</option>
        <option value="7ft">7ft</option>
        <option value="9ft">9ft</option>
        <option value="Custom">Customize Size Only in feets</option>
      </select>

      {size === 'Custom' && (
        <div>
          <label>Width:</label>
          <input type="number" name="width" value={customSize.width} onChange={handleCustomSizeChange} />
          <label>Height:</label>
          <input type="number" name="height" value={customSize.height} onChange={handleCustomSizeChange} />
        </div>
      )}

    </div>
  );
}

function AboutSeller() {
  return (
    <div>
      <h2>About Seller</h2>
      <p>Welcome to our ecommerce curtain store! We are committed to providing high-quality curtains and excellent customer service.</p>
    </div>
  );
}

function ReturnPolicy() {
  return (
    <div>
      <h2>Return Policy</h2>
      <p>Please note that we do not accept returns, only size alterations. For customized products, there are no returns or alterations.</p>
    </div>
  );
}



const Productinfo = () => {
  return (
    <div>
      <SizeOptions />
      <FabricCare />
      <AboutSeller />
      <ReturnPolicy />
    </div>
  )
}

export default Productinfo
