import React, { useState } from 'react';

const BillingDetails = () => {
  const [fullName, setFullName] = useState('');
  const [emailAddress, setEmailAddress] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [totalCartAmount, setTotalCartAmount] = useState(0);
  const [cartItems, setCartItems] = useState([
    { product: '', quantity: 0, price: 0 }
  ]);

  const handleFullNameChange = (e) => {
    setFullName(e.target.value);
  };

  const handleEmailAddressChange = (e) => {
    setEmailAddress(e.target.value);
  };

  const handlePhoneNumberChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const handleAddressChange = (e) => {
    setAddress(e.target.value);
  };

  const handleTotalCartAmountChange = (e) => {
    setTotalCartAmount(e.target.value);
  };

  const handleCartItemsChange = (index, field, value) => {
    const newCartItems = [...cartItems];
    newCartItems[index][field] = value;
    setCartItems(newCartItems);
  };

  const handleAddCartItem = () => {
    setCartItems([...cartItems, { product: '', quantity: 0, price: 0 }]);
  };

  const handleRemoveCartItem = (index) => {
    const newCartItems = [...cartItems];
    newCartItems.splice(index, 1);
    setCartItems(newCartItems);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const billingDetails = {
      fullName,
      emailAddress,
      phoneNumber,
      address,
      totalCartAmount,
      cartItems
    };
    fetch('/place-order', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(billingDetails)
    })
     .then((response) => response.json())
     .then((data) => console.log(data))
     .catch((error) => console.error(error));
  };

  return (
    <div>
      <h2>Billing Details</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input type="text" value={fullName} onChange={handleFullNameChange} />
        </label>
        <br />
        <label>
          Email Address:
          <input type="email" value={emailAddress} onChange={handleEmailAddressChange} />
        </label>
        <br />
        <label>
          Phone Number:
          <input type="tel" value={phoneNumber} onChange={handlePhoneNumberChange} />
        </label>
        <br />
        <label>
          Address:
          <textarea value={address} onChange={handleAddressChange} />
        </label>
        <br />
        <label>
          Total Cart Amount:
          <input type="number" value={totalCartAmount} onChange={handleTotalCartAmountChange} />
        </label>
        <br />
        <h3>Cart Items</h3>
        {cartItems.map((item, index) => (
          <div key={index}>
            <label>
              Product:
              <input type="text" value={item.product} onChange={(e) => handleCartItemsChange(index, 'product', e.target.value)} />
            </label>
            <br />
            <label>
              Quantity:
              <input type="number" value={item.quantity} onChange={(e) => handleCartItemsChange(index, 'quantity', e.target.value)} />
            </label>
            <br />
            <label>
              Price:
              <input type="number" value={item.price} onChange={(e) => handleCartItemsChange(index, 'price', e.target.value)} />
            </label>
            <br />
            <button type="button" onClick={() => handleRemoveCartItem(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={handleAddCartItem}>
          Add Cart Item
        </button>
        <br />
        <button type="submit">Place Order</button>
      </form>
    </div>
  );
};

export default BillingDetails;
