import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams for accessing route parameters



function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const { productId } = useParams(); // Access product ID from route parameter
  console.log('productId:', productId); // Verify the productId value

  async function fetchProduct(productId) {
    try {
      const response = await fetch(`/product/${productId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      // Check for valid JSON content type header
      if (response.headers.get('Content-Type') !== 'application/json') {
        throw new Error('Invalid response content type. Expected application/json');
      }
  
      const data = await response.text(); // Get the entire response as text
  
      // Try parsing as JSON (might still fail if HTML is returned)
      try {
        const product = await JSON.parse(data);
        return product; // Return the parsed product
      } catch (parseError) {
        console.error('Error parsing response as JSON:', parseError);
        // Handle the error (e.g., display an error message to the user)
        throw new Error('Error fetching product: Invalid response format');
      }
    } catch (error) {
      console.error('Error fetching product:', error);
      throw error; // Re-throw the error for handling in the component
    }
  }
  

  function displayProduct(product) {
    const productDetails = document.getElementById('product-details');
    productDetails.innerHTML = `
      <h2>Product Name: ${product.name}</h2>
      <p>Product Description: ${product.description}</p>
      <p>Price: ${product.price}</p>
    `;
  }
  fetchProduct(productId);


  useEffect(() => {
    fetch(`http://localhost:4000/product/${productId}`) // Fetch product based on ID
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => setProduct(data))
      .catch(error => setError(error.message)); // Catch and handle errors
  }, [productId]); // Dependency array to trigger fetch on ID change

  if (!product) {
    if (error) {
      return <div>Error: {error}</div>;
    } else {
      return <div>Loading product details...</div>;
    }
  }

  return (
    <div className="product-details">
      <img alt="" src={product.image} className="img-fluid product-thumbnail" />
      <h3 className="product-title">{product.name}</h3>
      <strong className="product-price">Rs{product.new_price}.00</strong>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
