import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing




function ShopContent() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]); // Initialize cartItems as an empty array
  const token = localStorage.getItem('token'); // Extract token to a separate variable

  

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:4000/allproducts');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchCartItems = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const response = await fetch('http://localhost:4000/getcart', {
            method:'POST',
            headers: {
              Accept: 'application/json',
              token:localStorage.getItem('token'),
              'Content-Type': 'application/json',
            },
          });
          const cartData = response.data;
          if (cartData && Object.keys(cartData).length > 0) {
            setCartItems(cartData);
          } else {
            setCartItems({}); // Set cartItems to an empty object if cartData is empty
          }
        }  catch (error) {
          if (error.response) {
            // handle error response
            console.error(error.response.data);
          } else {
            // handle other errors
            console.error(error);
          }
        }
      } else {
        console.error('Token not found in local storage');
      }
    };
      

    fetchProducts();
    fetchCartItems();
  }, [token]); // Use token in dependency array




  useEffect(() => {
    console.log('Cart items state:', cartItems);
  }, [cartItems]);


  // Use cartItems state to display the cart items
  const displayCartItems = () => {
    console.log('Rendering cart items...');
    return cartItems.map((item, index) => (
      <div key={index}>
        <h3>{item.name}</h3>
        <p>Quantity: {item.quantity}</p>
      </div>
    ));
  };

  return (
    <div className="untree_co-section product-section before-footer-section">
      <div className="container">
        <div className="row">
          {products.map(product => (
            <div key={product._id} className="col-12 col-md-4 col-lg-3 mb-5">
              <Link to={`/product/${product.id}`}> {/* Use Link for routing */}
                <img alt="" src={product.image} className="img-fluid product-thumbnail" />
                <h3 className="product-title">{product.name}</h3>
                <strong className="product-price">Rs{product.new_price}.00</strong>
              </Link>
            </div>
          ))}
        </div>
        <div className="row">
          <h2>Cart Items:</h2>
          {cartItems.length > 0 ? displayCartItems() : <p>No items in cart</p>}
        </div>
      </div>
    </div>
  );
}

export default ShopContent;
