import React, { useState } from 'react';
import { useContext } from 'react';
import axios from 'axios';
import { CartContext } from '../CartContext';

function Checkout() {
  const [billingDetails, setBillingDetails] = useState({
    name: '',
    email: '',
    address: '',
  });
  const { getTotalCartAmount, cartItems } = useContext(CartContext);

  const handleInputChange = (event) => {
    setBillingDetails({
      ...billingDetails,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      billingDetails,
      cartData: cartItems,
      totalCartAmount: getTotalCartAmount(),
    };
    // Save data to backend server
    axios.post('http://localhost:4000/save-order', data)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Billing Details</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={billingDetails.name} onChange={handleInputChange} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" name="email" value={billingDetails.email} onChange={handleInputChange} />
        </label>
        <br />
        <label>
          Address:
          <input type="text" name="address" value={billingDetails.address} onChange={handleInputChange} />
        </label>
        <br />
        <h2>Cart Data</h2>
        <ul>
          
        </ul>
        <p>Total Cart Amount: {getTotalCartAmount()}</p>
        <button type="submit">Save Order</button>
      </form>
    </div>
  );
}

export default Checkout;
