import React, { useState } from 'react';

const CartContent = () => {
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      image: 'images/product-1.png',
      name: 'Product 1',
      price: 49.00,
      quantity: 1,
    },
    {
      id: 2,
      image: 'images/product-2.png',
      name: 'Product 2',
      price: 49.00,
      quantity: 1,
    },
  ]);

  const [couponCode, setCouponCode] = useState('');
const [subtotal, setSubtotal] = useState(0);
const [total, setTotal] = useState(0);

const handleQuantityChange = (id, quantity) => {
  setCartItems(
    cartItems.map((item) => {
      if (item.id === id) {
        return { ...item, quantity };
      }
      return item;
    })
  );
  handleUpdateCart(); // update cart totals when quantity changes
};

const handleRemoveItem = (id) => {
  setCartItems(cartItems.filter((item) => item.id !== id));
  handleUpdateCart(); // update cart totals when item is removed
};

const handleApplyCoupon = () => {
  if (couponCode === 'DISCOUNT10') {
    // apply 10% discount
    setTotal(total * 0.9);
  } else if (couponCode === 'FREE SHIPPING') {
    // apply free shipping
    setTotal(subtotal);
  } else {
    alert('Invalid coupon code');
  }
};

const handleUpdateCart = () => {
  let newSubtotal = 0;
  cartItems.forEach((item) => {
    newSubtotal += item.price * item.quantity;
  });
  setSubtotal(newSubtotal);
  setTotal(newSubtotal); // update total without considering coupon
};

const handleProceedToCheckout = () => {
  window.location.href = '/Checkout';
};

  return (
    <div>
      <div className="hero">
        <div className="container">
          <div className="row justify-content-between">
            <div className="col-lg-5">
              <div className="intro-excerpt">
                <h1>Cart</h1>
              </div>
            </div>
            <div className="col-lg-7"></div>
          </div>
        </div>
      </div>
      

      <div className="untree_co-section before-footer-section">
        <div className="container">
          <div className="row mb-5">
            <form className="col-md-12" method="post">
              <div className="site-blocks-table">
                <table className="table">
                  <thead>
                    <tr>
                      <th className="product-thumbnail">Image</th>
                      <th className="product-name">Product</th>
                      <th className="product-price">Price</th>
                      <th className="product-quantity">Quantity</th>
                      <th className="product-total">Total</th>
                      <th className="product-remove">Remove</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cartItems.map((item) => (
                      <tr key={item.id}>
                        <td className="product-thumbnail">
                          <img src={item.image} alt="" className="img-fluid" />
                        </td>
                        <td className="product-name">
                          <h2 className="h5 text-black">{item.name}</h2>
                        </td>
                        <td>${item.price}</td>
                        <td>
                          <div
                            className="input-group mb-3 d-flex align-items-center quantity-container"
                            style={{ maxWidth: 120 }}
                          >
                            <div className="input-group-prepend">
                              <button
                                className="btn btn-outline-black decrease"
                                type="button"
                                onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                              >
                                &minus;
                              </button>
                            </div>
                            <input
                              type="text"
                              className="form-control text-center quantity-amount"
                              value={item.quantity}
                              onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value, 10))}
                            />
                            <div className="input-group-append">
                              <button
                                className="btn btn-outline-black increase"
                                type="button"
                                onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                              >
                                &plus;
                              </button>
                            </div>
                          </div>
                        </td>
                        <td>${item.price * item.quantity}</td>
                        <td>
                          <a
                            href="##"
                            className="btn btn-black btn-sm"
                            onClick={() => handleRemoveItem(item.id)}
                          >
                            X
                          </a>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>
          </div>
          </div>
          
          <div className="row">
  <div className="col-md-6">
    <div className="mb-5"> {/* Removed unnecessary row class */}
      <div className="col-md-6 mb-3 mb-md-0">
        <button className="btn btn-black btn-sm btn-block" onClick={handleUpdateCart}>
          Update Cart
        </button>
      </div>
      <div className="col-md-6 mb-3 mb-md-0">
        <input
          type="text"
          value={couponCode}
          onChange={(e) => setCouponCode(e.target.value)}
          placeholder="Enter coupon code"
        />
        <button className="btn btn-black btn-sm btn-block" onClick={handleApplyCoupon}>
          Apply Coupon
        </button>
      </div>
    </div>
  </div>
  <div className="col-md-6 mb-3 mb-md-0">
    <button className="btn btn-black btn-sm btn-block" onClick={handleProceedToCheckout}>
      Proceed to Checkout
    </button>
  </div>
</div>
</div>

          
          
    
  )
}

export default CartContent
