import { useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

async function fetchProduct(productId) {
  try {
    const headers = {
      'Content-Type': 'application/json'
    };
    const response = await fetch(`http://localhost:4000/allproducts/${productId}`, { headers });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    if (!response.headers.get('Content-Type').includes('application/json')) {
      throw new Error('Invalid response content type. Expected application/json');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching product:', error);
    throw error;
  }
}

function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [products, setProducts] = useState([]);

  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const { productId } = useParams();

  useEffect(() => {
    fetchProduct(productId)
      .then(data => {
        setProduct(data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
        if (error.response) {
          console.error('Error fetching product:', error.response.data);
        } else {
          console.error('Error fetching product:', error);
        }
      });
  }, [productId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="untree_co-section product-section before-footer-section">
      <div className="container">
        <div className="row">
          {products.map(product => (
            <div key={product._id} className="col-12 col-md-4 col-lg-3 mb-5">
              <Link to={`/product/${product.id}`}> {/* Use Link for routing */}
                <img alt="" src={product.image} className="img-fluid product-thumbnail" />
                <h3 className="product-title">{product.name}</h3>
                <strong className="product-price">Rs{product.new_price}.00</strong>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
