import { createContext, useState, useEffect } from 'react';




const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cartItems,setCartItems]=useState();
  const [isCartDataFetched, setIsCartDataFetched] = useState(false);


  useEffect(() => {
    if (!isCartDataFetched) {
      fetchCartData().then(data => {
        setCartItems(data);
        setIsCartDataFetched(true);
      });
    }
  }, [isCartDataFetched])

  const fetchCartData = async () => {
    const token = localStorage.getItem('token');
  if (token) {
    try {
      const response = await fetch('http://localhost:4000/getcart', {
        method:'POST',
        headers: {
          Accept: 'application/json',
          token:localStorage.getItem('token'),
          'Content-Type': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      const cartItems = await response.json();
      return cartItems;
    } catch (error) {
      console.error('Failed to fetch cart data:', error);
    }
  }
}

  

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev, [itemId]: (prev[itemId] || 0) + 1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/addtocart',{
        method:'POST',
        headers:{
          Accept:'application/json',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.text())
      .then((data)=>console.log(data));
    }
 }

  const removefromcart = (itemId) => {
    setCartItems((prev) => {
      const newCartItems = { ...prev };
      if (newCartItems[itemId] > 1) {
        newCartItems[itemId] -= 1;
      } else {
        delete newCartItems[itemId]; // Remove the item from the cart if quantity reaches 0
      }
      return newCartItems;
    });

    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/removefromcart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removefromcart  }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
