import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log("Cart updated:", cart);
    fetchCartData(); // Retrieve cart data on page load
  }, [cart]);

  const fetchCartData = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/getcart', {});
      const cartData = response.data;
      setCart(cartData);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const updateCartData = async (newCartData) => {
    try {
      const response = await axios.post('/updatecart', { cartData: newCartData });
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const addItemToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
    updateCartData([...cart, product]); // Update cart data on server
  };

  const removeItemFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id!== productId));
    updateCartData(cart.filter((item) => item.id!== productId)); // Update cart data on server
  };

  if (loading) { // <--- Display loading indicator while fetching data
    return <div>Loading...</div>;
  }

  return (
    <CartContext.Provider value={{ cart, addItemToCart, removeItemFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
