import { createContext, useState, useEffect } from 'react';




const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cartItems,setCartItems]=useState();

  useEffect(() => {
    fetchCartData(); // Retrieve cart data on page load
    
  }, [cartItems]);

  


  const fetchCartData = async () => {
    const token = localStorage.getItem('token');
  if (token) {
    try {
      const response = await fetch('http://localhost:4000/getcart', {
        method:'POST',
        headers: {
          Accept: 'application/json',
          token:localStorage.getItem('token'),
          'Content-Type': 'application/json',
        },
      });
      const cartData = response.data;
      if (cartData && Object.keys(cartData).length > 0) {
        setCartItems(cartData);
      } else {
        setCartItems({}); // Set cartItems to an empty object if cartData is empty
      }
    }  catch (error) {
      if (error.response) {
        // handle error response
        console.error(error.response.data);
      } else {
        // handle other errors
        console.error(error);
      }
    }
  } else {
    console.error('Token not found in local storage');
  }
};

  

  const addToCart = (itemId) => {
    setCartItems((prev) => ({...prev, [itemId]: (prev[itemId] || 0) + 1}));
    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/addtocart',{
        method:'POST',
        headers:{
          Accept:'application/json',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.text())
      .then((data)=>console.log(data));
    }
 }

  const removefromcart = (itemId) => {
    setCartItems((prev) => {
      const newCartItems = { ...prev };
      if (newCartItems[itemId] > 1) {
        newCartItems[itemId] -= 1;
      } else {
        delete newCartItems[itemId]; // Remove the item from the cart if quantity reaches 0
      }
      return newCartItems;
    });

    if(localStorage.getItem('token')){
      fetch('http://localhost:4000/removefromcart',{
        method:'POST',
        headers:{
          Accept:'application/form-data',
          token:localStorage.getItem('token'),
          'Content-Type':'application/json',
        },
        body:JSON.stringify({"itemId":itemId}),
      })
      .then((response)=>response.json())
      .then((data)=>console.log(data));
    }
 }

  

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removefromcart  }}>
      {children}
    </CartContext.Provider>
  );
};

export { CartProvider, CartContext };
