import React, { useState, useEffect } from 'react';

function ShopContent() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/allproducts')
    .then(response => response.json())
    .then(data => {
      console.log(data);
      setProducts(data)
    });
  }, []);

  return (
    <div className="untree_co-section product-section before-footer-section">
      <div className="container">
        <div className="row">
          {products.map(product => (
            <div key={product._id} className="col-12 col-md-4 col-lg-3 mb-5">
              <a className="product-item" href={`/product/${product.id}`}>
                <img alt="" src={`/images/${product.image}`} className="img-fluid product-thumbnail"/>
                <h3 className="product-title">{product.name}</h3>
                <strong className="product-price">${product.newPrice}.00</strong>

                <span className="icon-cross">
                  <img alt="" src="images/cross.svg" className="img-fluid"/>
                </span>
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ShopContent;
