import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams for accessing route parameters



function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const { productId } = useParams(); // Access product ID from route parameter
  console.log('productId:', productId); // Verify the productId value

  async function fetchProduct(productId) {
    try {
      const response = await fetch(`/product/${productId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const product = await response.json();
      return product;
    } catch (error) {
      console.error('Error fetching product:', error);
      return null; // or some default value
    }
  }

  function displayProduct(product) {
    const productDetails = document.getElementById('product-details');
    productDetails.innerHTML = `
      <h2>Product Name: ${product.name}</h2>
      <p>Product Description: ${product.description}</p>
      <p>Price: ${product.price}</p>
    `;
  }
  fetchProduct(productId);


  useEffect(() => {
    fetch(`http://localhost:4000/product/${productId}`)
      .then(response => response.json())
      .then(data => {
        setProduct(data);
        if (data) { // Call displayProduct only if product is fetched successfully
          displayProduct(data);
        }
      })
      .catch(error => setError(error.message));
  }, [productId]);
  if (product && !error) {
    displayProduct(product); // Call displayProduct when product is available
  }

  return (
    <div className="product-details">
      <img alt="" src={product.image} className="img-fluid product-thumbnail" />
      <h3 className="product-title">{product.name}</h3>
      <strong className="product-price">Rs{product.new_price}.00</strong>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
